func scanAddresses(rows *sql.Rows, firstOnly bool) (vv []*Address, n int64, err error) {
	for rows.Next() {
		n++

		var v0 int64
		var v1 []byte
		var v2 sql.NullString
		var v3 string

		err = rows.Scan(
			&v0,
			&v1,
			&v2,
			&v3,
		)
		if err != nil {
			return vv, n, err
		}

		v := &Address{}
		v.Id = v0
		err = json.Unmarshal(v1, &v.Lines)
		if err != nil {
			return nil, n, err
		}
		if v2.Valid {
			a := v2.String
			v.Town = &a
		}
		v.Postcode = v3

		var iv interface{} = v
		if hook, ok := iv.(sqlgen2.CanPostGet); ok {
			err = hook.PostGet()
			if err != nil {
				return vv, n, err
			}
		}

		vv = append(vv, v)

		if firstOnly {
			if rows.Next() {
				n++
			}
			return vv, n, rows.Err()
		}
	}

	return vv, n, rows.Err()
}

